import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import './App.css';

const socket = io('http://localhost:5001');

function App() {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const MAX_MESSAGE_LENGTH = 200; // Set maximum message length

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') {
      // Prevent sending empty messages
      return;
    }
    socket.emit('message', message);
    setMessage('');
  };

  useEffect(() => {
    // Fetch existing messages from backend
    axios.get('http://localhost:5001/api/chat')
      .then(response => setMessages(response.data))
      .catch(error => console.log(error));

    // Listen for new messages
    socket.on('message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, msg]);
    });

    return () => {
      socket.off('message');
    };
  }, []);

  return (
    <div>
      <h1>Fun Chat</h1>
      <form onSubmit={sendMessage}>
        <input
          type="text"
          value={message}
          onChange={(e) => {
            if (e.target.value.length <= MAX_MESSAGE_LENGTH) {
              setMessage(e.target.value);
            }
          }}
          maxLength={MAX_MESSAGE_LENGTH} // Limit input length
        />
        <button type="submit">Send</button>
      </form>
      <div>
        {messages.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>
      <p>{MAX_MESSAGE_LENGTH - message.length} characters remaining</p> {/* Display character counter */}
    </div>
  );
}

export default App;
